import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { db } from "../services/firebase";
import { doc, getDoc } from "firebase/firestore";

const Building = () => {
    const { id } = useParams(); // Get building ID from URL
    const [building, setBuilding] = useState(null);

    useEffect(() => {
        const fetchBuilding = async () => {
            const docRef = doc(db, "buildings", id);
            const docSnap = await getDoc(docRef);

            if (docSnap.exists()) {
                setBuilding(docSnap.data());
                console.log("Fetched Building Data:", docSnap.data()); // Debugging step
            } else {
                console.error("No such building found!");
            }
        };

        fetchBuilding();
    }, [id]);

    if (!building) {
        return <div className="text-white text-center mt-10">Loading building details...</div>;
    }

    return (
        <div className="min-h-screen">
            {/* Navigation Bar */}
            <nav className="flex justify-between items-center bg-white py-4 px-10 shadow-md">
                <Link to="/" className="text-2xl font-bold text-gold">
                    Campus <span className="text-navy">Connect</span>
                </Link>

                {/* Search Bar */}
                <div className="relative w-1/3">
                    <input 
                        type="text" 
                        placeholder="🔍 What are you looking for?..." 
                        className="w-full px-4 py-2 rounded-full border border-gray-300 focus:outline-none"
                    />
                </div>

                {/* Navigation Links */}
                <div className="space-x-6 text-navy font-medium">
                    <Link to="/" className="hover:underline">Home</Link>
                    <Link to="/about" className="hover:underline">About</Link>
                    <Link to="/services" className="hover:underline">Services</Link>
                    <Link to="/contact" className="hover:underline">Contact</Link>
                </div>

                {/* Sign In Button */}
                <Link to="/signin" className="bg-gold text-white px-5 py-2 rounded-full hover:bg-[#B48225]">
                    Sign In
                </Link>
            </nav>

            {/* Main Content */}
            <div className="flex h-screen">
                {/* Left Side: Building Details */}
                <div className="w-1/2 bg-navy text-white p-10">
                    {/* Building Image */}
                    {building.building_image ? (
                        <img
                            src={building.building_image}
                            alt={building.building_name || "Building Image"}
                            className="w-full h-64 object-cover rounded-lg mb-4"
                        />
                    ) : (
                        <p className="text-gray-300">No image available</p>
                    )}

                    {/* Building Details */}
                    <h1 className="text-5xl font-bold">{building.building_name || "Unknown Building"}</h1>
                    <p className="text-lg mt-4">{building.description || "No description available."}</p>

                    {/* Contact Details */}
                    <div className="mt-6">
                        {/* Phone Numbers */}
                        {building.phone_num ? (
                            <>
                                <p><strong>📞 Phone:</strong></p>
                                <ul className="list-disc ml-6">
                                    {Array.isArray(building.phone_num)
                                        ? building.phone_num.map((phone, index) => <li key={index}>{phone}</li>)
                                        : <li>{building.phone_num}</li>}
                                </ul>
                            </>
                        ) : (
                            <p><strong>📞 Phone:</strong> N/A</p>
                        )}

                        {/* Emails */}
                        {building.email ? (
                            <>
                                <p><strong>📧 Email:</strong></p>
                                <ul className="list-disc ml-6">
                                    {Array.isArray(building.email)
                                        ? building.email.map((email, index) => <li key={index}>{email}</li>)
                                        : <li>{building.email}</li>}
                                </ul>
                            </>
                        ) : (
                            <p><strong>📧 Email:</strong> N/A</p>
                        )}

                        {/* Operating Hours */}
                        <p className="mt-4"><strong>🕒 Operating Hours:</strong></p>
                        <ul className="list-disc ml-6">
                            {building.operating_hours?.length > 0 ? (
                                building.operating_hours.map((hour, index) => (
                                    <li key={index}>{hour}</li>
                                ))
                            ) : (
                                <li>No hours available</li>
                            )}
                        </ul>

                        {/* Services Offered */}
                        <p className="mt-4"><strong>🛠 Services Offered:</strong></p>
                        <ul className="list-disc ml-6">
                            {building.services_offered?.length > 0 ? (
                                building.services_offered.map((service, index) => (
                                    <li key={index}>{service}</li>
                                ))
                            ) : (
                                <li>No services available</li>
                            )}
                        </ul>

                        {/* Wheelchair Accessibility */}
                        <p className="mt-4"><strong>♿ Wheelchair Accessible:</strong> {building.wheelchair_accessible ? "Yes" : "No"}</p>
                    </div>
                </div>

                {/* Right Side: Google Maps Embed */}
                <div className="w-1/2 bg-gold flex items-center justify-center">
                    {building.lat && building.lng ? (
                        <iframe
                            width="100%"
                            height="100%"
                            frameBorder="0"
                            style={{ border: 0 }}
                            src={`https://www.google.com/maps/embed/v1/place?q=${building.lat},${building.lng}&key=YOUR_GOOGLE_MAPS_API_KEY`}
                            allowFullScreen
                        ></iframe>
                    ) : (
                        <p className="text-center text-black">Map location not available</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Building;
