import React, { useState } from "react";
import { Link } from "react-router-dom";
import { searchBuildings } from "../services/firestoreSearchService"; // Import search function

const Welcome = () => {
    const [searchQuery, setSearchQuery] = useState("");
    const [searchResults, setSearchResults] = useState([]);

    // Handle search input
    const handleSearch = async () => {
        if (!searchQuery.trim()) return;
        
        const results = await searchBuildings(searchQuery.trim().toLowerCase());
        setSearchResults(results);
    };

    return (
        <div className="min-h-screen flex flex-col items-center justify-center bg-navy text-white">
            <h1 className="text-5xl font-extrabold">Welcome to</h1>
            <h2 className="text-6xl font-extrabold text-gold mt-2">CampusConnect!</h2>
            <p className="mt-4 text-lg text-gray-300 text-center px-6">
                CampusConnect is here to help you navigate our campus according to your necessities and desired destination.
            </p>

            {/* Search Bar */}
            <div className="mt-6 w-96 flex">
                <input 
                    type="text" 
                    placeholder="🔍 Search for a building..." 
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="input input-bordered w-full rounded-l-full text-black px-4"
                />
                <button 
                    onClick={handleSearch}
                    className="bg-gold text-white px-5 py-2 rounded-r-full shadow-lg hover:bg-[#B48225]"
                >
                    Search
                </button>
            </div>

            {/* Search Results */}
            <div className="mt-4">
                {searchResults.length > 0 ? (
                    searchResults.map((building) => (
                        <Link 
                            key={building.id} 
                            to={`/building/${building.id}`} 
                            className="block bg-white text-navy px-4 py-2 rounded shadow-lg mt-2 hover:bg-gray-200"
                        >
                            {building.building_name}
                        </Link>
                    ))
                ) : (
                    searchQuery && <p className="text-gray-400 mt-2">No results found.</p>
                )}
            </div>

            {/* Quick Navigation Buttons */}
            <div className="mt-6 flex flex-wrap justify-center gap-4">
                <Link to="/building/library" className="btn btn-primary bg-navy text-white px-5 py-2 rounded-full shadow-lg hover:bg-[#001A4A]">
                    Library
                </Link>
                <Link to="/building/cafe" className="btn bg-gold text-white px-5 py-2 rounded-full shadow-lg hover:bg-[#B48225]">
                    Cafe Campesino
                </Link>
                <Link to="/building/storm-dome" className="btn bg-gold text-white px-5 py-2 rounded-full shadow-lg hover:bg-[#B48225]">
                    Storm Dome
                </Link>
            </div>
        </div>
    );
};

export default Welcome;
