import { db } from "./firebase";
import { collection, getDocs, query, where } from "firebase/firestore";

/**
 * Search buildings by user input query.
 * Matches building_name, search_keywords, or services_offered.
 */
export const searchBuildings = async (searchQuery) => {
    if (!searchQuery) return []; // Don't search if query is empty

    const buildingsRef = collection(db, "buildings");
    let results = [];

    // ðŸ”¹ Firestore query to search by exact keyword match
    const keywordQuery = query(
        buildingsRef,
        where("search_keywords", "array-contains", searchQuery.toLowerCase()) // Case-insensitive search
    );

    const keywordSnapshot = await getDocs(keywordQuery);
    keywordSnapshot.forEach((doc) => {
        results.push({ id: doc.id, ...doc.data() });
    });

    // âœ… Direct Navigation if only ONE match is found
    if (results.length === 1) {
        return results;
    }

    // ðŸ”¹ Fallback: Search by building_name (partial match)
    if (results.length === 0) {
        console.log(`ðŸ” No exact matches for '${searchQuery}', trying partial match...`);
        const nameQuery = query(
            buildingsRef,
            where("building_name", ">=", searchQuery),
            where("building_name", "<=", searchQuery + "\uf8ff")
        );

        const nameSnapshot = await getDocs(nameQuery);
        nameSnapshot.forEach((doc) => {
            results.push({ id: doc.id, ...doc.data() });
        });
    }

    // âœ… Direct Navigation if only ONE match is found after fallback
    if (results.length === 1) {
        return results;
    }

    // ðŸ”¹ Fallback: Search services_offered (PARTIAL MATCH included)
    if (results.length === 0) {
        console.log(`ðŸ” No name matches for '${searchQuery}', searching by services...`);
        
        const allDocs = await getDocs(buildingsRef);
        allDocs.forEach((doc) => {
            const data = doc.data();
            if (data.services_offered?.some(service => service.toLowerCase().includes(searchQuery.toLowerCase()))) {
                results.push({ id: doc.id, ...data });
            }
        });
    }

    return results;
};
