{/*display google map with directions from user's location to destination*/}
// Import the functions needed from the SDKs 
import React, { useEffect, useRef } from "react";

const MapNavigation = ({  destination, userLocation, travelMode, onRouteCalculated  }) => {
    // Create a reference to the map div element
    const mapRef = useRef(null);
    const directionsServiceRef = useRef(null);
    const directionsRendererRef = useRef(null);

    useEffect(() => {
        if (!window.google || !window.google.maps) {
            console.error("Google Maps API not loaded.");
            return;
        }

        //initialize google maps & center on destination
        const map = new window.google.maps.Map(mapRef.current, {
            zoom: 16,//default zoom level
            center: userLocation || destination, //center map on destination
        });

        //create services for directions and rendering
        directionsServiceRef.current = new window.google.maps.DirectionsService();
        directionsRendererRef.current = new window.google.maps.DirectionsRenderer();
        directionsRendererRef.current.setMap(map);

        // Cleanup function to avoid memory leaks
        return () => {
            if (directionsRendererRef.current) {
                directionsRendererRef.current.setMap(null); // Remove the renderer from the map
            }
        };
    }, [userLocation, destination]); // Only re-run if userLocation or destination changes

        // //request user's location as lat & long coordinates
        // navigator.geolocation.getCurrentPosition((position) => {
        //     const userLocation = {
        //         lat: position.coords.latitude,
        //         lng: position.coords.longitude,
        //     };

        // Calculate and display the route
        useEffect(() => {
            if (userLocation && destination && directionsServiceRef.current && directionsRendererRef.current) {
                const request = {
                    origin: userLocation,
                    destination: destination,
                    travelMode: window.google.maps.TravelMode[travelMode], // Use travelMode prop
                };




        directionsServiceRef.current.route(request, (result, status) => {
            if (status === window.google.maps.DirectionsStatus.OK) {
                directionsRendererRef.current.setDirections(result);

                // Extract distance and duration
                const route = result.routes[0].legs[0];
                const distance = route.distance.text;
                const duration = route.duration.text;

                // Pass distance and duration back to parent component
                onRouteCalculated({
                    [travelMode.toLowerCase()]: { distance, duration },
                });

            } else {
                console.error("Directions request failed:", status);
            }
        });
       }
    }, [destination, userLocation, travelMode, onRouteCalculated]);// Re-run effect if `userLocation` or `destination` changes

    //map div to display map
    return <div ref={mapRef} style={{ width: "100%", height: "100%" }} />;
};

export default MapNavigation;
