import React, { useEffect, useState } from "react"; 
import { useParams, Link, useNavigate } from "react-router-dom";
import { db } from "../services/firebase";
import { doc, getDoc } from "firebase/firestore";
import { searchBuildings } from "../services/firestoreSearchService";

const Building = () => {
    const { id } = useParams(); // Get building ID from URL
    const navigate = useNavigate(); // Enables navigation
    const [building, setBuilding] = useState(null);
    const [loading, setLoading] = useState(true); // Loading state
    const [query, setQuery] = useState(""); // Search input state
    const [searchResults, setSearchResults] = useState([]); // Search results
    const [showDropdown, setShowDropdown] = useState(false); // Controls dropdown visibility

    useEffect(() => {
        const fetchBuilding = async () => {
            try {
                const docRef = doc(db, "buildings", id);
                const docSnap = await getDoc(docRef);

                if (docSnap.exists()) {
                    setBuilding(docSnap.data());
                    console.log("Fetched Building Data:", docSnap.data()); // Debugging step
                } else {
                    console.error("No such building found!");
                    setBuilding(null);
                }
            } catch (error) {
                console.error("Error fetching building:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchBuilding();
    }, [id]);

    // Handle search submission (Same as Page 1)
    const handleSearch = async (event) => {
        event.preventDefault();
        if (query.trim() === "") return; // Ignore empty searches

        const results = await searchBuildings(query);
        setSearchResults(results);
        setShowDropdown(true); // Show dropdown with results

        // Auto-navigate if only one result exists
        if (results.length === 1) {
            navigate(`/building/${results[0].id}`);
        }
    };

    // If still loading, show a loading message
    if (loading) {
        return <div className="text-white text-center mt-10">Loading building details...</div>;
    }

    // If no building found, show an error message
    if (!building) {
        return (
            <div className="text-white text-center mt-10">
                <h2 className="text-3xl font-bold">Building Not Found</h2>
                <p>Sorry, the requested building does not exist.</p>
                <Link to="/" className="text-gold underline mt-4 block">Return to Home</Link>
            </div>
        );
    }

    return (
        <div className="min-h-screen">
            {/* Navigation Bar */}
            <nav className="flex justify-between items-center bg-white py-4 px-10 shadow-md relative">
                <Link to="/" className="text-2xl font-bold text-gold">
                    Campus <span className="text-navy">Connect</span>
                </Link>

                {/* Search Bar (Now Works Like Page 1) */}
                <form onSubmit={handleSearch} className="relative w-1/3 flex">
                    <input 
                        type="text" 
                        placeholder="🔍 What are you looking for?..." 
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        className="w-full px-4 py-2 rounded-full border border-gray-300 focus:outline-none text-black" // Ensure text is visible
                    />
                    <button 
                        type="submit"
                        className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gold text-white px-4 py-1 rounded-full hover:bg-[#B48225]"
                    >
                        Search
                    </button>
                </form>


                {/* Search Results Dropdown (Matches Page 1) */}
                {showDropdown && searchResults.length > 0 && (
                    <div className="absolute top-16 left-1/3 bg-white w-1/3 border shadow-lg rounded-lg p-2 z-10">
                        {searchResults.map((building) => (
                            <Link 
                                key={building.id} 
                                to={`/building/${building.id}`}
                                className="block px-4 py-2 hover:bg-gray-200 text-navy"
                            >
                                {building.building_name}
                            </Link>
                        ))}
                    </div>
                )}

                {/* Navigation Links */}
                <div className="space-x-6 text-navy font-medium">
                    <Link to="/" className="hover:underline">Home</Link>
                    <Link to="/about" className="hover:underline">About</Link>
                    <Link to="/services" className="hover:underline">Services</Link>
                    <Link to="/contact" className="hover:underline">Contact</Link>
                </div>

                {/* Sign In Button */}
                <Link to="/signin" className="bg-gold text-white px-5 py-2 rounded-full hover:bg-[#B48225]">
                    Sign In
                </Link>
            </nav>

            {/* Main Content */}
            <div className="flex h-screen">
                {/* Left Side: Building Details */}
                <div className="w-1/2 bg-navy text-white p-10">
                    {/* Building Image */}
                    {building?.building_image ? (
                        <img
                            src={building.building_image}
                            alt={building?.building_name || "Building Image"}
                            className="w-full h-64 object-cover rounded-lg mb-4"
                        />
                    ) : (
                        <p className="text-gray-300">No image available</p>
                    )}

                    {/* Building Details */}
                    <h1 className="text-5xl font-bold">{building?.building_name || "Unknown Building"}</h1>
                    <p className="text-lg mt-4">{building?.description || "No description available."}</p>

                    {/* Services Offered */}
                    {building?.services_offered?.length > 0 ? (
                        <ul className="list-disc ml-6">
                            {building.services_offered.map((service, index) => <li key={index}>{service}</li>)}
                        </ul>
                    ) : (
                        <p><strong>🛠 Services Offered:</strong> No services available</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Building;
