import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { searchBuildings } from "../services/firestoreSearchService";

const Welcome = () => {
    const [searchQuery, setSearchQuery] = useState("");
    const [results, setResults] = useState([]);
    const navigate = useNavigate();

    // Handle search function
    const handleSearch = async (event) => {
        event.preventDefault(); // Prevent default form submission
        if (!searchQuery.trim()) return;

        const searchResults = await searchBuildings(searchQuery);
        setResults(searchResults);

        if (searchResults.length === 1) {
            // If only one result, navigate directly
            navigate(`/building/${searchResults[0].id}`);
        }
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-navy text-white">
            <h1 className="text-5xl font-bold">Welcome to <span className="text-gold">CampusConnect!</span></h1>
            <p className="mt-2 text-lg">Find campus buildings quickly!</p>

            {/* Search Form */}
            <form onSubmit={handleSearch} className="mt-6 flex">
                <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="Search for a building..."
                    className="px-4 py-2 rounded-l-lg text-black w-80"
                />
                <button
                    type="submit"
                    className="bg-gold px-4 py-2 rounded-r-lg font-bold"
                >
                    Search
                </button>
            </form>

            {/* Display search results */}
            <div className="mt-4">
                {results.length === 0 ? (
                    <p className="text-gray-300">No results found.</p>
                ) : (
                    <ul className="bg-white text-black p-4 rounded-lg shadow-lg">
                        {results.map((building) => (
                            <li
                                key={building.id}
                                className="cursor-pointer hover:bg-gray-200 p-2 rounded"
                                onClick={() => navigate(`/building/${building.id}`)}
                            >
                                {building.building_name}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default Welcome;
