import { db } from "./firebase"; // Import Firebase config
import { collection, getDocs, updateDoc, doc } from "firebase/firestore";

export const updateDocumentsWithId = async () => {
    const buildingsRef = collection(db, "buildings"); // Reference 'buildings' collection
    const snapshot = await getDocs(buildingsRef);

    snapshot.forEach(async (document) => {
        const docRef = doc(db, "buildings", document.id);
        await updateDoc(docRef, {
            id: document.id, // Assign document name as 'id'
        });
        console.log(`Updated ${document.id} with id field`);
    });
};
